mapper.select:
SELECT * from tempregister
WHERE 1=1 
 AND id=#{id}
 AND unit=#{unit}
 AND tmerName=#{tmerName}
 AND minTemp=#{minTemp}
 AND maxTemp=#{maxTemp}
 AND miniScale=#{miniScale}
 AND manufacturer=#{manufacturer}
 AND tmterNo=#{tmterNo}
 AND sampleNo=#{sampleNo}
 AND certificateNo=#{certificateNo}
 AND result=#{result}
 AND createUserID=#{createUserID}
 AND createDate=#{createDate}

SELECT count(1) from tempregister
WHERE 1=1 
 AND id=#{id}
 AND unit=#{unit}
 AND tmerName=#{tmerName}
 AND minTemp=#{minTemp}
 AND maxTemp=#{maxTemp}
 AND miniScale=#{miniScale}
 AND manufacturer=#{manufacturer}
 AND tmterNo=#{tmterNo}
 AND sampleNo=#{sampleNo}
 AND certificateNo=#{certificateNo}
 AND result=#{result}
 AND createUserID=#{createUserID}
 AND createDate=#{createDate}

SELECT * from tempregister
WHERE 1=1 
 AND id=#{id}
 AND unit=#{unit}
 AND tmerName=#{tmerName}
 AND minTemp=#{minTemp}
 AND maxTemp=#{maxTemp}
 AND miniScale=#{miniScale}
 AND manufacturer=#{manufacturer}
 AND tmterNo=#{tmterNo}
 AND sampleNo=#{sampleNo}
 AND certificateNo=#{certificateNo}
 AND result=#{result}
 AND createUserID=#{createUserID}
 AND createDate=#{createDate}
order by ${extLimit.sort} ${extLimit.dir}
 limit ${extLimit.start}, ${extLimit.limit}

SELECT t.*,u.userName as createUserName from tempregister t 
left join user u on t.createUserID = u.id
WHERE 1=1 
 AND id=#{id}
 AND unit=#{unit}
 AND tmerName=#{tmerName}
 AND minTemp=#{minTemp}
 AND maxTemp=#{maxTemp}
 AND miniScale=#{miniScale}
 AND manufacturer=#{manufacturer}
 AND tmterNo=#{tmterNo}
 AND sampleNo=#{sampleNo}
 AND certificateNo=#{certificateNo}
 AND result=#{result}
 AND createUserID=#{createUserID}
 AND createDate=#{createDate}
order by ${extLimit.sort} ${extLimit.dir}
 limit ${extLimit.start}, ${extLimit.limit}
