mapper.select:
SELECT * from tempregister
WHERE 1=1 
 AND id=#{id}
 AND unit=#{unit}
 AND tmerName=#{tmerName}
 AND minTemp=#{minTemp}
 AND maxTemp=#{maxTemp}
 AND miniScale=#{miniScale}
 AND manufacturer=#{manufacturer}
 AND tmterNo=#{tmterNo}
 AND sampleNo=#{sampleNo}
 AND certificateNo=#{certificateNo}
 AND result=#{result}
 AND createUserID=#{createUserID}
 AND createDate=#{createDate}
 AND tempregisterpointName=#{tempregisterpointName}

SELECT count(1) from tempregister
WHERE 1=1 
 AND id=#{id}
 AND unit=#{unit}
 AND tmerName=#{tmerName}
 AND minTemp=#{minTemp}
 AND maxTemp=#{maxTemp}
 AND miniScale=#{miniScale}
 AND manufacturer=#{manufacturer}
 AND tmterNo like '%${tmterNo}%'
 AND sampleNo=#{sampleNo}
 AND certificateNo=#{certificateNo}
 AND result=#{result}
 AND createUserID=#{createUserID}
 AND createDate=#{createDate}

SELECT * from tempregister
WHERE 1=1 
 AND id=#{id}
 AND unit=#{unit}
 AND tmerName=#{tmerName}
 AND minTemp=#{minTemp}
 AND maxTemp=#{maxTemp}
 AND miniScale=#{miniScale}
 AND manufacturer=#{manufacturer}
 AND tmterNo like '%${tmterNo}%'
 AND sampleNo=#{sampleNo}
 AND certificateNo=#{certificateNo}
 AND result=#{result}
 AND createUserID=#{createUserID}
 AND createDate=#{createDate}
order by ${extLimit.sort} ${extLimit.dir}
 limit ${extLimit.start}, ${extLimit.limit}

SELECT t.*,u.realName as createUserName from tempregister t 
left join user u on t.createUserID = u.id
WHERE 1=1 
 AND id=#{id}
 AND unit=#{unit}
 AND tmerName=#{tmerName}
 AND minTemp=#{minTemp}
 AND maxTemp=#{maxTemp}
 AND miniScale=#{miniScale}
 AND manufacturer=#{manufacturer}
 AND tmterNo like '%${tmterNo}%'
 AND sampleNo=#{sampleNo}
 AND certificateNo=#{certificateNo}
 AND result=#{result}
 AND createUserID=#{createUserID}
 AND createDate=#{createDate}
order by ${extLimit.sort} ${extLimit.dir}
 limit ${extLimit.start}, ${extLimit.limit}

SELECT count(1) from tempregister
WHERE 1=1 
 AND id=#{id}
 AND unit=#{unit}
 AND tmerName=#{tmerName}
 AND minTemp=#{minTemp}
 AND maxTemp=#{maxTemp}
 AND miniScale=#{miniScale}
 AND manufacturer=#{manufacturer}
 AND tmterNo like '%${tmterNo}%'
 AND sampleNo=#{sampleNo}
 AND certificateNo=#{certificateNo}
 AND result=#{result}
 AND createUserID=#{createUserID}
 AND createDate=#{createDate}
 and DATE(createDate) >= DATE(#{startDate})
 and DATE(createDate)  <=  DATE(#{endDate})

SELECT t.*,u.realName as createUserName from tempregister t 
left join user u on t.createUserID = u.id
WHERE 1=1 
 AND id=#{id}
 AND unit=#{unit}
 AND tmerName=#{tmerName}
 AND minTemp=#{minTemp}
 AND maxTemp=#{maxTemp}
 AND miniScale=#{miniScale}
 AND manufacturer=#{manufacturer}
 AND tmterNo like '%${tmterNo}%'
 AND sampleNo=#{sampleNo}
 AND certificateNo=#{certificateNo}
 AND result=#{result}
 AND createUserID=#{createUserID}
 AND createDate=#{createDate}
 and DATE(createDate) >= DATE(#{startDate})
 and DATE(createDate)  <=  DATE(#{endDate})
order by ${extLimit.sort} ${extLimit.dir}
 limit ${extLimit.start}, ${extLimit.limit}

select te.* from (
	select max(id) id from tempregister where tmterNo like '%${value}%' 
	group by tmterNo
) t left join tempregister te on te.id = t.id
order by te.id desc limit 20;

select te.* from (
	select max(id) id from tempregister where tmterNo=#{value} 
) t left join tempregister te on te.id = t.id;
